    onUploadFirmaError: function(src, data) {
        var me = this;
        var titleView = me.getTitleView();
        var msg = 'ErrorType: ' + data.errorType;
        switch (data.errorType) {
            case 'FileSize':
                msg = 'Este archivo es demasiado grande: ' + Ext.util.Format.fileSize(data.fileSize) + '. El tamao mximo de subida es ' + Ext.util.Format.fileSize(data.maxFileSize) + '.';
                break;
            case 'QueueLength':
                msg = 'La longitud de la cola es demasiado larga: ' + data.queueLength + '. La longitud mxima de la cola es ' + data.maxQueueLength + '.';
                break;
        }
        Coomuce.Util.ShowMessage({
            type: "ERROR",
            title: titleView,
            msg: msg
        });
    },
